// src/helper/streaming/sse.ts
import { StreamingApi } from "../../utils/stream.js";
import { stream } from "./index.js";
var SSEStreamingApi = class extends StreamingApi {
  constructor(writable, readable) {
    super(writable, readable);
  }
  async writeSSE(message) {
    const data = message.data.split("\n").map((line) => {
      return `data: ${line}`;
    }).join("\n");
    const sseData = [message.event && `event: ${message.event}`, data, message.id && `id: ${message.id}`].filter(Boolean).join("\n") + "\n\n";
    await this.write(sseData);
  }
};
var setSSEHeaders = (context) => {
  context.header("Transfer-Encoding", "chunked");
  context.header("Content-Type", "text/event-stream");
  context.header("Cache-Control", "no-cache");
  context.header("Connection", "keep-alive");
};
var streamSSE = (c, cb) => {
  return stream(c, async (originalStream) => {
    const { readable, writable } = new TransformStream();
    const stream2 = new SSEStreamingApi(writable, readable);
    originalStream.pipe(stream2.responseReadable).catch((err) => {
      console.error("Error in stream piping: ", err);
      stream2.close();
    });
    setSSEHeaders(c);
    try {
      await cb(stream2);
    } catch (err) {
      console.error("Error during streaming: ", err);
    } finally {
      await stream2.close();
    }
  });
};
export {
  SSEStreamingApi,
  streamSSE
};
